mapper.select:
SELECT 

FROM category a

WHERE a.id = #{id}

SELECT 

FROM category a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name = #{name}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM category a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE category SET 	
	name = #{name},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
