mapper.select:
SELECT

FROM qrtz_schedule_job a

WHERE a.id = #{id}

SELECT
FROM qrtz_schedule_job a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.job_name LIKE '%'||#{jobName}||'%' '%'+#{jobName}+'%' concat('%',#{jobName},'%')
AND a.alias_name LIKE '%'||#{aliasName}||'%' '%'+#{aliasName}+'%' concat('%',#{aliasName},'%')
AND a.job_group LIKE '%'||#{jobGroup}||'%' '%'+#{jobGroup}+'%' concat('%',#{jobGroup},'%')
AND a.is_sync = #{isSync}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM qrtz_schedule_job a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE qrtz_schedule_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
